mapper.insert:
insert into sys_user (id, username, name,
                                    phone, email, address,
                                    create_time, create_user, update_time,
                                    update_user, deleted, password
)
values (#{id,jdbcType=BIGINT}, #{username,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR},
        #{phone,jdbcType=VARCHAR}, #{email,jdbcType=VARCHAR}, #{address,jdbcType=VARCHAR},
        #{createTime,jdbcType=TIMESTAMP}, #{createUser,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP},
        #{updateUser,jdbcType=VARCHAR}, #{deleted,jdbcType=BIT}, #{password,jdbcType=LONGVARCHAR}
       )

mapper.select:
select
    u.*
from sys_user u



        and u.name like #{nameLike}


       and u.phone = #{phone}


       and u.email = #{email}
